targeted_name = input("Enter the targeted github username : ").strip(' ')
print('')

spider_launcher(targeted_name)

with open(f'{path_to_scrap}/leaked_email.txt', 'r') as file:
    lines = file.readlines()
    if lines:
        content = lines[0]
        print(f"User {targeted_name}'s email is : {content}")
        print("The email is saved in scrapy_core/leaked_email.txt")
    else:
        print(f"No email was found for the user {targeted_name}")
